import React, { Component } from "react";
import "../Styling/Display.scss";
export class DisplayShift extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const shiftList = JSON.parse(localStorage.getItem("shiftList"));
    return <React.Fragment>
         <div className="mainDisplay">
                <button onClick={back}>
                  <i class="fa fa-arrow-left" aria-hidden="true"></i>
                </button>
              </div>
        {
            shiftList.map(shiftListItem => {
                const {
                    startDate,
                    startTime,
                    endTime,
                    repeatType,
                    shiftValue,
                    weekdayOnly,
                    weekdays,
                  } = JSON.parse(shiftListItem);
                  const { back } = this.props;
                  const selectedWeekdays = weekdays.filter((item) => item.value);
                  return (
                    <div className="form-cmp">
                      <div className="mainDisplay">
                        <div>Start Date :</div>
                        <div>{startDate}</div>
                      </div>
                      <div className="mainDisplay">
                        <div>Select Repeat Type :</div>
                        <div>{repeatType}</div>
                      </div>
                      <div className="mainDisplay">
                        <div>Select Shift :</div>
                        <div>{shiftValue}</div>
                      </div>
                      <div className="mainDisplay">
                        <div>Select Start Time :</div>
                        <div>{startTime}</div>
                      </div>
                      <div className="mainDisplay">
                        <div>Select End Time :</div>
                        <div>{endTime}</div>
                      </div>
                      <div className="mainDisplay">
                        <div>Week Day :</div>
                        <div>
                          {selectedWeekdays.map((item) => (
                            <div className="weekdayItem">{item.text}</div>
                          ))}
                        </div>
                      </div>
                      <div className="mainDisplay">
                        <div>Week Day Only :</div>
                        <div>{weekdayOnly ? "Yes" : "No"}</div>
                      </div>
                    </div>
                  );
            })
        }
    </React.Fragment>
  }
}

export default DisplayShift;

const transform = (apnaWalaObject) => {
  return {
    start_date: apnaWalaObject.startDate,
    arrival_time: apnaWalaObject.startTime,
    departure_time: apnaWalaObject.endTime,
    repeat: apnaWalaObject.repeatType,
    shift_availablity: apnaWalaObject.startDate,
  };
};
