import React, { Component } from "react";
import Input from "../components/Input";
import Select from "../components/Select";
import Day from "./Day";
import "../Styling/Form.scss";
class Form extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputfield: "",
      weekdays: [
        {
          id: 1,
          text: "Monday",
          value: false,
        },
        {
          id: 2,
          text: "Tuesday",
          value: false,
        },
        {
          id: 3,
          text: "Wednesday",
          value: false,
        },
        {
          id: 4,
          text: "Thursday",
          value: false,
        },
        {
          id: 5,
          text: "Friday",
          value: false,
        },
        {
          id: 6,
          text: "Saturday",
          value: false,
        },
        {
          id: 7,
          text: "Sunday",
          value: false,
        },
      ],
    };
  }

  handleInputField = (event) => {
    this.setState({
      inputfield: event.target.value,
    });
  };

  handleTopic = (event) => {
    this.setState({
      topic: event.target.value,
    });
  };

  handleSubmit = (event) => {
    alert(`${this.state.inputfield} ${this.state.topic}`);
    event.preventDefault();
  };

  render() {
    const optionValue = [
      {
        value: "none",
        optionText: "None",
      },
      {
        value: "daily",
        optionText: "Daily",
      },
      {
        value: "weekly",
        optionText: "Weekly",
      },
    ];

    const shiftValue = [
      {
        value: "Morning Shift - 5am to 9am",
        shiftText: "Morning Shift - 5am to 9am",
      },
    ];
    const { weekdays } = this.state;
    return (
      <form className={"form-cmp"} onSubmit={this.handleSubmit}>
        <div className="head">
          <h4>Select Start date or Shift Times</h4>
          <p>each row represent a shift</p>
        </div>
        <div className="input-wrapper">
          <Input
            text={"Select Start Date"}
            type={"date"}
            placeholderValue="MM/DD/YYYY"
            onChange={this.handleInputField}
          />
          <Select
            optionsArray={optionValue}
            onChange={this.handleTopic}
            text={"Select Repeat type"}
          />
          <Select
            shiftArray={shiftValue}
            onChange={this.handleTopic}
            text={"Select Shift"}
          />
          <Input
            text={"Select Start Time"}
            type={"time"}
            onChange={this.handleInputField}
          />
          <Input
            text={"Select End Time"}
            type={"time"}
            onChange={this.handleInputField}
          />
          <div>
          <h4>pleade select the day of the week </h4>
          <div className="days-wrapper">
            {weekdays.map((day) => {
              return <Day dayText={day.text} value={day.value} />;
            })}
          </div>
        </div>
        </div>
        <div>
          <button className={"add-btn"} type="submit">
            ADD +
          </button>
        </div>
      </form>
    );
  }
}

export default Form;
