import React from "react";
import '../Styling/Day.scss'
const weekdays = (props) => {
    const { weekData, onWeekSelect } = props;
    const { id, text, value } = weekData;
  return (
    <div className="day">
      <div>{text}</div>
      <div
        className={`circle ${value ? "active" : ""}`}
        onClick={() => {
          onWeekSelect(id);
        }}
      ></div>
    </div>
  );
};

export default weekdays;
