import React, { Component } from "react";
import "../Styling/Display.scss";
export class DisplayShift extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const localStorageData = JSON.parse(localStorage.getItem("formValue"));
    const {
      startDate,
      startTime,
      endTime,
      repeatType,
      shiftValue,
      weekdayOnly,
      weekdays,
    } = localStorageData;
    const {back} = this.props;
    const selectedWeekdays = weekdays.filter((item) => item.value);
    return (
      <div className="form-cmp">
        <button
            onClick={back}
          style={{ color: "red", borderRadius: "50%", cursor: "pointer" }}
        >
          X
        </button>
        <div className="mainDisplay">
          <div>Start Date :</div>
          <div>{startDate}</div>
        </div>
        <div className="mainDisplay">
          <div>Select Repeat Type :</div>
          <div>{repeatType}</div>
        </div>
        <div className="mainDisplay">
          <div>Select Shift :</div>
          <div>{shiftValue}</div>
        </div>
        <div className="mainDisplay">
          <div>Select Start Time :</div>
          <div>{startTime}</div>
        </div>
        <div className="mainDisplay">
          <div>Select End Time :</div>
          <div>{endTime}</div>
        </div>
        <div className="mainDisplay">
          <div>Week Day :</div>
          <div>
            {selectedWeekdays.map((item) => (
              <div className="weekdayItem">{item.text}</div>
            ))}
          </div>
        </div>
        <div className="mainDisplay">
          <div>Week Day Only :</div>
          <div>{weekdayOnly ? "Yes" : "No"}</div>
        </div>
      </div>
    );
  }
}

export default DisplayShift;
