import React, { Component } from "react";
import Input from '../components/Input'
import "../Styling/Form.scss";
class Form extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputfield: "",
      topic: "none",
    };
  }

  handleInputField = (event) => {
    this.setState({
      inputfield: event.target.value,
    });
  };

  handleTopic = (event) => {
    this.setState({
      topic: event.target.value,
    });
  };

  handleSubmit = (event) => {
    alert(`${this.state.inputfield} ${this.state.topic}`);
    event.preventDefault();
  };

  render() {
    return (
      <form className={"form-cmp"} onSubmit={this.handleSubmit}>
        <Input/>
        <div className={"shifts"}>
          <div>
            <h4>Select Start date or Shift Times</h4>
            <p>each row represent a shift</p>
          </div>
          <div className={"shifts-cmp"}>
            <label>Select Start Date</label>
            <input
              type="date"
              value={this.state.inputfield}
              onChange={this.handleInputField}
            />
          </div>
          <div className={"shifts-cmp"}>
            <label>Select Repeat Type</label>
            <select value={this.state.topic} onChange={this.handleTopic}>
              <option value="none">none</option>
              <option value="daily">Daily</option>
              <option value="weekly">Weekly</option>
            </select>
          </div>
          <div className={"shifts-cmp"}>
            <label>Select Shift</label>
            <select value={this.state.topic} onChange={this.handleTopic}>
              <option value="none">morning shift</option>
            </select>
          </div>
          <div className={"shifts-cmp"}>
            <label>Select Start Time</label>
            <input
              type="time"
              value={this.state.inputfield}
              onChange={this.handleInputField}
            />
          </div>
          <div className={"shifts-cmp"}>
            <label>Select End Time</label>
            <input
              type="time"
              value={this.state.inputfield}
              onChange={this.handleInputField}
            />
          </div>
        </div>
        <div>
          <h4>pleade select the day of the week </h4>
        </div>
        <div>
          <button className={"add-btn"} type="submit">
            ADD +
          </button>
        </div>
      </form>
    );
  }
}

export default Form;
